<?phpclass DB {    protected static $instance;    protected $credential = "automocion";    # server    private static $vector_configuration_database = array(        "avantio" => array(            "db_host" => "localhost",            "db_port" => "3306",            "db_user" => "automoci_user",            "db_pass" => "Berruezin007",            "db_name" => "automoci_inmobiliaria_dos",            "db_charset" => "UTF-8"        ),        "local" => array(            "db_host" => "localhost",            "db_port" => "3306",            "db_user" => "root",            "db_pass" => "Berruezin23",            "db_name" => "pisosenm_inmobiliaria",            "db_charset" => "UTF-8"        ),        "automocion" => array(            "db_host" => "localhost",            "db_port" => "3306",            "db_user" => "root",            "db_pass" => "Berruezin23",            "db_name" => "tiendapi_automocion",            "db_charset" => "UTF-8"        ),        "servidor_tiendapisos" => array(            "db_host" => "localhost",            "db_port" => "3306",            "db_user" => "tiendapi_user",            "db_pass" => "Perretin771",            "db_name" => "tiendapi_inmobiliaria",            "db_charset" => "UTF-8"        ),        "servidor_automocion" => array(            "db_host" => "localhost",            "db_port" => "3306",            "db_user" => "automoci_user",            "db_pass" => "Perretin771",            "db_name" => "automoci_inmobiliaria",            "db_charset" => "UTF-8"        )    );    public function __construct() {        // empty constructor    }    public static function getInstance() {        if(empty(self::$instance)) {            $db_info = self::$vector_configuration_database["servidor_tiendapisos"];            try {                self::$instance = new wpdb($db_info['db_user'],$db_info['db_pass'],$db_info['db_name'],'localhost');            } catch(PDOException $error) {                echo $error->getMessage();            }        }        return self::$instance;    }// end function    private function connectPDO()    {        $db_info = self::$vector_configuration_database["servidor"];        self::$instance = new PDO("mysql:host=".$db_info['db_host'].';port='.$db_info['db_port'].';dbname='.$db_info['db_name'], $db_info['db_user'], $db_info['db_pass']);        self::$instance->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_SILENT);        self::$instance->query('SET NAMES utf8');        self::$instance->query('SET CHARACTER SET utf8');    } // end function    /**     * @return string     */    public function getCredential()    {        return $this->credential;    }    /**     * @param string $credential     */    public function setCredential($credential)    {        $this->credential = $credential;    }    public static function setCharsetEncoding() {        if (self::$instance == null) {            self::connect();        }        self::$instance->exec(            "SET NAMES 'utf8';            SET character_set_connection=utf8;            SET character_set_client=utf8;            SET character_set_results=utf8"        );    }// end function}// end class?>