<?phpclass Service{    protected $table      = 'dynamic_services';    protected $primaryKey = 'id';    protected $allowedFields = ['id', 'language','text_title'];    /**     * @param $xml     * @param $avantio_credentials     * @param $ts     * @param $ts_referencia     */    public function insertServices($xml,$avantio_credentials)    {        $wpdb = Database::getInstance();        //echo "llega aqui<br>";        $counter = 0;        //p_($avantio_credentials['ACTIVED_LANGUAGES']);        foreach($xml->Service as $service){            if ($counter < 2){                //p_($service);                //print_r($service);            }            # de momento solo guardamos 20 servicios            foreach($service->Name as $trad){                if((string)$trad->Language == "es" && $counter < 20){                    //echo "entra aqui<br>";                    //if (in_array($trad->Language , $avantio_credentials["ACTIVED_LANGUAGES"])){                    # insert term                    $actions = array(                        (string)$trad->Text                    );                    foreach ($actions as $key) {                        $my_cat = array(                            'description' => $key,                            'slug' => sanitize_title($key)                        );                        //p_($my_cat);                        if(!term_exists($key, 'property_features', $my_cat) ){                            //echo "esta entrando<br>";                            $return = wp_insert_term($key, 'property_features', $my_cat);                            //p_($return );                        } // end if                    } // end foreach                    $counter++;                }// end if            }// end foreach        } // end foreach    } // end function    /**     *     */    private function add_two_primary_keys()    {        $forge = \Config\Database::forge();        $forge->addKey('id', TRUE);        $forge->addKey('language', TRUE);    } // end function    private function insertar_actualizar()    {        $data = [            'id'            => "",            'language'      => "",            'text_title'    => ""        ];        $this->save($data);    } // end function}// end class?>